import React, {useState} from 'react'
import {Button, Alert, Navbar, Container, Nav, Form, FormControl} from 'react-bootstrap'
import "../styles/Home.css"
import {useAuth} from '../contexts/AuthContext'
import { Link, useHistory } from 'react-router-dom'

export default function HomePage() {
    const [error, setError] = useState('')
    const {currentUser, logout} = useAuth()
    const history = useHistory()

    async function handleLogout() {
        setError('')

        try {
            await logout()
            history.push('/login')
        } catch {
            setError('Failed to log out')
        }
    }

    return (
        <>
        {error && <Alert variant='danger'>{error}</Alert>}
        <Navbar expand="lg" bg="dark" variant="dark">

            {/* <Container> */}
            <Navbar.Brand >
                <Link to="/" className="homeBrand">
                <img src='img/Youtube.png' alt='YouTube-Logo' className='youtubeLogoHome'/>
                        Youtube Custom Playlists
                </Link>
            </Navbar.Brand>
            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
            <Navbar.Collapse id="responsive-navbar-nav">
            <Nav className="me-auto my-2 my-lg-0" style={{maxHeigh: '100px'}} navbarScroll>
                <Nav.Link> <Link to="/">My Playlists</Link></Nav.Link>
            </Nav>
            <Form className="d-flex">
                <FormControl
                    type="search"
                    placeholder="Search"
                    className="mr-2"
                    aria-label="Search"
                />
                <Button>Search</Button>
            </Form>
            <Nav>
                <Nav.Link disabled>{currentUser.email}</Nav.Link>
                <Button onClick={handleLogout}>Logout</Button>

            </Nav>
            </Navbar.Collapse>
            {/* </Container> */}

        
        </Navbar>
        </>
       
    )
}
