import '../styles/Playlist.css'
import PlaylistItem from './PlaylistItem'
import '../styles/VideoItem.css'

export default function PlaylistList({videos, handleVideoSelect}) {
    // const [channelTitle, setChannelTitle] = useState('')
    // const [channelId, setChannelId] = useState('')
    // const [thumbnail, setThumbnail] = useState('')
    // const [title, setTitle] = useState('')
    // const [videoId, setVideoId] = useState('')
    const [videoDoc, setVideoDoc] = useState({})

    function getVideoData(video) {
        
    }

    const renderedVideos = videos.map((video) => {
        // getVideoData(video)
        setVideoDoc({
            channelTitle: video.videoDoc.get('channel-title'),
            channelId: video.videoDoc.get('channelId'),
            thumbnail: video.videoDoc.get('thumbnail'),
            title: video.videoDoc.get('title'),
            videoId: video.videoDoc.get('videoId')
        })
        return <PlaylistItem video={videoDoc} handleVideoSelect={handleVideoSelect}/>

    });

    return (
        <div className='mt-5'>
            <div className="playlist-item">
                {/* {console.log(videos)} */}
                {/* {console.log(videos[0].videoDoc.get('thumbnail'))} */}
                {/* <img className="thumbnail"
                src={props.video.snippet.thumbnails.medium.url} 
                alt={props.video.snippet.description} 
                onClick={() => props.handleVideoSelect(props.video)}/>
                <div className="videoItem-title">
                    {props.video.snippet.title}
                </div>
                <div className="videoItem-channel">
                    Channel: {props.video.snippet.channelTitle}
                </div> */}


            <div className="videoList">
                {renderedVideos}
            </div>
        

            </div>
        </div>
        
    
    )
}
